import React from 'react';
import Table from "./Table";
import {
    atom, useRecoilState, useRecoilValue
} from "recoil";

export const tableState = atom({
    key: 'tableState',
    default: {
        tableSize: {
            row: 4,
            col: 4,
        }
    },
});

export const gameState = atom({
    key: 'gameState',
    default:
        [
            []
        ],
});

const startGame = (row, col) => {
    const table = [];
    for (let i = 0; i < row; i++) {
        const rowData = [];
        table.push(rowData);
        for (let j = 0; j < col; j++) {
            rowData.push(0);
        }
    }
    return table;
}

const Game2048 = () => {
    const [gameTable, setGameTable] = useRecoilState(gameState);
    const { tableSize } = useRecoilValue(tableState);

    const onClickStart = () => {

        const table = [...gameTable];

        for (let i = 0; i < tableSize.row; i ++) {
            table[i] = [...gameTable[i]];
            for (let j = 0; j < tableSize.col; j++) {
                table[i][j] = 0;
            }
        }
        console.log(table);
        setGameTable(table);
    };

    return (
        <>
            <Table/>
            <button onClick={onClickStart}>시작!!</button>
        </>
    );
};

export default Game2048;
