import React from 'react';
import Table from "./Table";
import {
    atom, useRecoilState, useRecoilValue
} from "recoil";

export const tableState = atom({
    key: 'tableState',
    default: {
        tableSize: {
            row: 4,
            col: 4,
        }
    },
});

export const gameTableState = atom({
    key: 'gameTableState',
    default: [],
});

const makeRandomBlock = (gameTable) => {
    const table = [...gameTable];
    const emptyTable = [];

    table.forEach((row, i) => {
        row.forEach((col, j) => {
            if (table[i][j] === 0) {
                emptyTable.push([i, j]);
            }
        });
    })

    const randomBlock = emptyTable[Math.floor(Math.random() * emptyTable.length)];
    table[randomBlock[0]][randomBlock[1]] = 2;

    return table;
}

const startGame = (row, col) => {
    const table = [];

    for (let i = 0; i < row; i++) {
        const rowData = [];
        table.push(rowData);
        for (let j = 0; j < col; j++) {
            rowData.push(0);
        }
    }

    return table;
}

const Game2048 = () => {
    const [gameTable, setGameTable] = useRecoilState(gameTableState);
    const { tableSize } = useRecoilValue(tableState);

    const onClickStart = () => {
        let table = startGame(tableSize.row, tableSize.col)
        setGameTable(table);
    };

    const onClickTest = () => {
        const table = makeRandomBlock(gameTable);
        console.log(table);
        setGameTable(table);
    }
    return (
        <>
            <Table/>
            <button onClick={onClickStart}>시작!!</button>
            <button onClick={onClickTest}>Test</button>
        </>
    );
};

export default Game2048;
