import React from 'react';
import Table from "./Table";
import {
    atom, useRecoilState, useRecoilValue
} from "recoil";

export const tableState = atom({
    key: 'tableState',
    default: {
        tableSize: {
            row: 4,
            col: 4,
        }
    },
});

export const gameTableState = atom({
    key: 'gameTableState',
    default: [],
});

const makeRandomBlock = (gameTable) => {
    console.log(gameTable);
    // const table = gameTable;
    // const size = table.length * table[0].length;
    // const randomBlock = Math.floor(Math.random() * size);
    //
    // const row = parseInt(randomBlock / table.length);
    // const col = randomBlock % table.length;
    //
    // if (table[row][col] !== 0) {
    //     makeRandomBlock(table);
    // } else {
    //     table[row][col] = 2;
    // }
    // return table;
}

const startGame = (row, col) => {
    const table = [];

    for (let i = 0; i < row; i++) {
        const rowData = [];
        table.push(rowData);
        for (let j = 0; j < col; j++) {
            rowData.push(0);
        }
    }
    return table;
}

const Game2048 = () => {
    const [gameTable, setGameTable] = useRecoilState(gameTableState);
    const { tableSize } = useRecoilValue(tableState);

    const onClickStart = () => {
        let table = startGame(tableSize.row, tableSize.col)
        makeRandomBlock(table);
        setGameTable(table);
    };

    return (
        <>
            <Table/>
            <button onClick={onClickStart}>시작!!</button>
            <button onClick={makeRandomBlock}>Test</button>
        </>
    );
};

export default Game2048;
