import React from 'react';
import {useRecoilValue} from "recoil";
import Td from "./Td";
import {tableState} from "./Game2048";

const Tr = ({ rowIndex }) => {
    const { table: tableSize } = useRecoilValue(tableState);

    return (
        <tr>
            {Array(tableSize.col).fill().map((td, i) => <Td key={i} rowIndex={rowIndex} colIndex={i}/>)}
        </tr>
    );
};

export default Tr;
